# the main goal of this project is to get flags of each country from wikipedia and store it in a folder with the country name as the name of the file

import requests
from bs4 import BeautifulSoup
from PIL import Image

def scrape(url):
    r = requests.get(url)
    htmlContent = r.content
    soup = BeautifulSoup(htmlContent, "html.parser")
    return soup

def parse(soup):
    anchor_link_tag = soup.find("a", class_="mw-file-description")
    flag_webpage_url = "https://en.wikipedia.org" + anchor_link_tag.get("href")
    soup_svg = scrape(flag_webpage_url)
    div_containing_img_link = soup_svg.find("div", class_="fullImageLink")
    img_tag = div_containing_img_link.find("img")
    img_src ="https:" + img_tag.get("src")
    return img_src

def output(img_url, name_country):
    img = Image.open(requests.get(img_url, stream = True).raw)
    file_name = "Flags\\"+ name_country + ".png"
    img.save(file_name)

